import classes from "./Cart.module.css";
import Modal from "../UI/Modal";
import CartItem from "./CartItem";
import Checkout from "./Checkout";
import { useContext, useState } from "react";
import cartContext from "../../Store/cart-context";

const Cart = (props) => {
  const [inCheckout, setInCheckout] = useState(false);
  const cartCtx = useContext(cartContext);

  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;

  // const hasItems = cartCtx.items.length >= 1;

  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };

  const cartItemAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  };

  const orderHandler = (event) => {
    setInCheckout(true);
  };

  const submitOrderHandler = (userData) => {
    fetch(
      "https://reactmovies-http-post-default-rtdb.europe-west1.firebasedatabase.app/orders.json",
      {
        method: "POST",
        body: JSON.stringify({
          user: userData,
          orderedItems: cartCtx.items,
        }),
      }
    );
  };

  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          id={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={() => cartItemRemoveHandler(item.id)}
          onAdd={() => cartItemAddHandler(item)}
        />
      ))}
    </ul>
  );

  const modalActions = (
    <div className={classes.actions}>
      <button className={classes["button-alt"]} onClick={props.onClose}>
        Close
      </button>
      <button
        disabled={!(cartCtx.items.length >= 1)}
        className={classes.button}
        onClick={orderHandler}
      >
        Order
      </button>
    </div>
  );

  return (
    <Modal onClose={props.onClose}>
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>{totalAmount}</span>
      </div>
      {inCheckout && (
        <Checkout
          onOrder={orderHandler}
          onCancel={props.onClose}
          submitData={submitOrderHandler}
        />
      )}
      {!inCheckout && modalActions}
    </Modal>
  );
};

export default Cart;
