import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "../Meals/MealItem/MealItem";
import { useEffect } from "react";

const AvailableMeals = (props) => {
  let mealsList = [];

  const fetchMealsHandler = async () => {
    try {
      const response = await fetch(
        "https://reactmovies-http-post-default-rtdb.europe-west1.firebasedatabase.app.json/"
      );

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      // Eftersom firebase ger oss objekt vill vi göra om det till en array som vi sedan mapar upp.
      const responseData = await response.json();
      console.log(responseData);

      // Här gör vi transformationen
      const loadedMeals = [];

      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }

      mealsList = loadedMeals.map((meal) => (
        <MealItem
          id={meal.id}
          key={meal.id}
          name={meal.name}
          description={meal.description}
          price={meal.price}
        />
      ));
    } catch (error) {}
  };

  useEffect(() => {
    fetchMealsHandler();
  }, [fetchMealsHandler]);

  return (
    <section className={classes.meals}>
      <Card>{mealsList}</Card>
    </section>
  );
};

export default AvailableMeals;
